	public LongestSubstrWithoutRepeatingChars(String inputString, int expedtedOutput) {
		this.inputString = inputString;
		this.expedtedOutput = expedtedOutput;
	}

	@Test
	public void testLongest() {
		int output = solution.lengthOfLongestSubstring(inputString);
		System.out.printf("Testing input: %2s, expected: %s, calculated: %s%n",
				inputString, expedtedOutput, output);
		Assert.assertEquals(expedtedOutput, output);
	}

	static class Solution {
		public int lengthOfLongestSubstring(String s) {
			int maxLength = 0;
			final Map<Character, Integer> map = new HashMap<>(s.length());
			for (int i = 0, j = 0; i < s.length(); i++) {
				Integer existing = map.put(s.charAt(i), i);
				if (existing != null) {
					j = Math.max(j, existing + 1);
				}
				maxLength = Math.max(maxLength, i - j + 1);
			}
			return maxLength;
		}

		public int lengthOfLongestSubstring1(String s) {
			int current_len = 0, max_length = 0;
			String current_nrcs = "";
			Set<Character> visited = new HashSet<>();
			for (int i = 0; i < s.toCharArray().length; i++) {
				final char c = s.charAt(i);
				if (!visited.contains(c)) {
					visited.add(c);
					current_len++;
					max_length = Math.max(max_length, current_len);
					current_nrcs = current_nrcs.concat(c + "");
				}
				else {
					int index = current_nrcs.indexOf(c);
					current_nrcs = current_nrcs
						.substring(index + 1, current_nrcs.length())
						.concat(c + "");
					current_len = current_nrcs.length();
					// visited.clear();
					// for (char x : current_nrcs.toCharArray()) {
					// visited.remove(x);
					// }
				}
			}
			return max_length;
		}

		/***
		 * the basic idea is, keep a hashmap which stores the characters in string as keys
		 * and their positions as values, and keep two pointers which define the max
		 * substring. move the right pointer to scan through the string , and meanwhile
		 * update the hashmap. If the character is already in the hashmap, then move the
		 * left pointer to the right of the same character last found. Note that the two
		 * pointers can only move forward.
		 * @param s
		 * @return
		 */
		public int lengthOfLongestSubstring2(String s) {
			if (s.length() == 0)
				return 0;
			HashMap<Character, Integer> map = new HashMap<Character, Integer>();
			int max = 0;
			for (int i = 0, j = 0; i < s.length(); ++i) {
				if (map.containsKey(s.charAt(i))) {
					j = Math.max(j, map.get(s.charAt(i)) + 1);
				}
				map.put(s.charAt(i), i);
				max = Math.max(max, i - j + 1);
			}
			return max;
		}

		// The idea is use a hash set to track the longest substring without repeating
		// characters so far, use a fast pointer j to see if character j is in the hash
		// set or not, if not, great, add it to the hash set, move j forward and update
		// the max length, otherwise, delete from the head by using a slow pointer i until
		// we can put character j to the hash set.

		public int lengthOfLongestSubstring4(String s) {
			int i = 0, j = 0, max = 0;
			Set<Character> set = new HashSet<>();

			while (j < s.length()) {
				if (!set.contains(s.charAt(j))) {
					set.add(s.charAt(j++));
					max = Math.max(max, set.size());
				}
				else {
					set.remove(s.charAt(i++));
				}
			}

			return max;
		}
	}

